const App = () => {
  // const course = 'Half Stack application development'
  // const part1 = 'Fundamentals of React'
  // const exercises1 = 10
  // const part2 = 'Using props to pass data'
  // const exercises2 = 7
  // const part3 = 'State of a component'
  // const exercises3 = 14

  //1.3
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  // const part1 = {
  //   name: 'Fundamentals of React',
  //   exercises: 10
  // }
  // const part2 = {
  //   name: 'Using props to pass data',
  //   exercises: 7
  // }
  // const part3 = {
  //   name: 'State of a component',
  //   exercises: 14
  // }

  const Header = (props) => {
    return(
      <h1>{props.course}</h1>
    )
  }
  const Content = (props) => {
    return (
      <div>
        <Part part={props[0].name} exercise={props[0].exercises}/>
        <Part part={props[1].name} exercise={props[1].exercises}/>
        <Part part={props[2].name} exercise={props[2].exercises}/>
      </div>
    )
  }
  const Part = (props) => {
    return (
      <p>{props.part} {props.exercise}</p>
    )
  }
  const Total = (props) => {
    return (
      <p>Number of exercises {props.total_score}</p>
    )
  }
  return (
    <div>
      <Header course={course} />
      <Content parts={parts}/>
      <Total total_score={parts[0].exercises + parts[1].exercises + parts[2].exercises} />
    </div>
  )

}

export default App
